extension WidgetListEx on List<Widget> {
  List<Widget> separated(Widget separator) {
    List<Widget> list = map((element) => <Widget>[element, separator])
        .expand((e) => e)
        .toList();
    if (list.isNotEmpty) list = list..removeLast();
    return list;
  }
}

